import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';


const ListItem = () => {
  const [list, setList] = useState([])
  const [post, setPost] = useState([])
  const [error, setError] = useState(null)
  const [newPost, setNewPost] = useState('');


  const fetchUser = async () => {
    try {
      const response = await axios.get("https://jsonplaceholder.typicode.com/users")
      setList(response.data.map(res => res.name))
    }
    catch (err) {
      setError("something went wrong")
    }
  }
  useEffect(() => {
    fetchPost()
    fetchUser()
  }, [])

  const fetchPost = async () => {
    try {
      const response = await axios.get("https://jsonplaceholder.typicode.com/users")
      setPost(response.data.map(res => res.name))
    }
    catch (err) {
      setError("something went wrong")
    }
  }
  const deletePost = async (id) => {
    try {
      await axios.delete(`https://jsonplaceholder.typicode.com/posts/${id}`);

      const newPost = post.filter((_, index) => index !== id);
      setPost(newPost);
    } catch (err) {
      setError("something went wrong while deleting");
    }
  };

  const createPost = async () => {
    try {
      const response = await axios.post("https://jsonplaceholder.typicode.com/posts", {
        name: newPost
      });
      setPost([...post, response.data.name]);
      setNewPost('');
    } catch (err) {
      setError("Something went wrong  post");
    }
  };

  const updatePost = async (id, updateName) => {
    await axios.put(`https://jsonplaceholder.typicode.com/posts/${id}`,{name: updateName})
    try{
      const updatePosts = post.map((posts) => posts.id === id ? {...posts,updateName} : posts)
      setPost(updatePosts)
      setNewPost("")
    }
    catch(err){
      setError("something went wrong update postList")
    } 
  }


  return (
    <div>
      <header>
        <h3>ListItem</h3>
        {error && <p>{error}</p>}
        {list.map((listItem, index) => (
          <ul key={index}>
            <li>{listItem}</li>
            <button>edit</button>
          </ul>
        ))}
      </header>
      <div>

        {/* new data post */}
        <h3>ListItems</h3>
        {error && <p>{error}</p>}
        <input
          type="text"
          value={newPost}
          onChange={(e) => setNewPost(e.target.value)}
          placeholder="Enter the name"
        />
        <button onClick={createPost}>Post</button>
        {/* data edit in postList */}
        {post.map((p, index) => (
          <ul key={"p" + index}>
            <li>{p}</li>
            <input type="text" value={newPost} onChange={() => setNewPost(p,index)} />
           <button data-testid={"put" + index} onClick={() => updatePost(index, p)}>Edit</button> 
            <button data-testid={"delete" + index} onClick={() => deletePost(index)}>
              Remove
            </button>

          </ul>
        ))}
      </div>
    </div>
  );
};

export default ListItem;
