import React, { useEffect, useState } from 'react'
import api from './api'
import _ from 'lodash'

const MobileApi = () => {
  const [mobiles, setMobiles] = useState([])
  const [error, setError] = useState(null)

  const fetchItem = async () => {
    try {
      const response = await api.get("/mobiles")
      setMobiles(response.data)
    }
    catch (err) {
      setError("something went wrong")
    }
  }
  useEffect(() => {
    fetchItem()
  },[])

  return (
    <div>
      {error && <div>{error}</div>}
      {(_.isEmpty) ? (

      <table>
         <h1>mobiles list:</h1>
        <thead>
          <tr>
            <th>Model:</th>
            <th>Brand:</th>
            <th>Price:</th>
            <th>Spec:</th>
          </tr>
        </thead>
        <tbody>
          {_.map(mobiles,(item) => (
                 <tr key={item.id}>
                 <td>{item.model}</td>
                 <td>{item.brand}</td>
                 <td>{item.price}</td>
                 <td>{item.spec}</td>
               </tr>
          ))}

        </tbody>
      </table>)
      : (<p>List is empty</p>
          )}
    </div>
  )
}

export default MobileApi
